import {Component, Input, OnInit} from '@angular/core';
import {PersonService} from './person.service';
import {Person} from "./person";

@Component({
  selector: 'app-person',
  templateUrl: './person.component.html',
  styleUrls: ['./person.component.css']
})
export class PersonComponent implements OnInit {

  public persons?: Person[];
  public personSearch?: Person;
  public title: string = 'کاربران';
  public personTitle: string = '';
  @Input() sourceLoad: boolean = true;
  public editModeTitle: string = 'ویرایش کاربر';
  public editMode: boolean = false;
  public editLoadId: string = '';

  constructor(
    private personService: PersonService
  ) {
    this.personSearch = new Person();
  }

  ngOnInit(): void {
    this.onLoad();
  }

  onLoad() {
    this.personService.getAllPerson().subscribe(persons => {
      this.persons = persons;
      console.log(persons);
    });
  }

  reload(): void {
    if (this.editMode) {
      console.log('if');
    } else {
      this.onLoad();
    }
  }

  editModeBack(editMode: boolean): void {
    this.editLoadId = '';
    this.editMode = editMode;
    this.onLoad();
  }

  deletePerson(person: Person) {
    this.personService.deletePerson(person).subscribe(data => {
      console.log(data);
    })
  }

  switchToEditMode(loadId: string) {
    this.editLoadId = loadId;
    this.editModeTitle = 'تعریف کاربر جدید';
    this.editMode = true;
  }
}
<div class="m-3 p-2">
  <div class="card">
    <header class="bg-light row m-0">
      <h2 class="col-sm-7 lh-4" *ngIf="!editMode; else editModeTitle">{{ title }} {{ personTitle }}</h2>
      <ng-template #editModeTitle>
        <h2 class="col-sm-7 lh-4">
          {{ editModeTitle }}
        </h2>
      </ng-template>
      <div class="text-end col-sm-5 lh-lg">
        <app-header-tools
          (editModeEmitter)="editModeBack($event)"
          (reloadEmitter)="reload()"
          [editMode]="editMode"
        ></app-header-tools>
      </div>
    </header>
    <article class="">
      <table class="table table-bordered table-striped table-hover"
             *ngIf="!editMode">
        <thead>
        <tr class>
          <th>نام</th>
          <th>نام خانوادگی</th>
          <th>جنسیت</th>
          <th>وضعیت</th>
        </tr>
        </thead>
        <tbody>
        <tr class="" *ngFor="let person of persons"
            (dblclick)="switchToEditMode(person.id)"
        >
          <td> {{ person.firstName }}</td>
          <td> {{ person.lastName }}</td>
          <td *ngIf="person.gender; else female">مرد</td>
          <ng-template #female>
            <td>زن</td>
          </ng-template>
          <td *ngIf="person.status;else deactive">فعال</td>
          <ng-template #deactive>
            <td>غیر فعال</td>
          </ng-template>
        </tr>
        </tbody>
      </table>
      <div class="" *ngIf="editMode">
        <app-person-edit
          [personId]="editLoadId"
          (editModeEmitter)="editModeBack($event)"
        >
        </app-person-edit>
      </div>
    </article>
  </div>
</div>
