import {Component, OnInit} from '@angular/core';
import {Package} from "./package";
import {PackageService} from "./package.service";

@Component({
  selector: 'app-package-edit',
  templateUrl: './package-edit.component.html',
  styleUrls: ['./package-edit.component.css']
})
export class PackageEditComponent implements OnInit {
  public switchToGrid: any;
  public package: Package;

  constructor(
    private packageService: PackageService,
  ) {
    this.package = new Package();
  }

  ngOnInit(): void {
  }

  buttonClicked($event: string) {
    switch ($event) {
      case 'save':
        this.save();
        break;
      case 'reload':
        break;
      case 'return':
        this.switchToGrid.emit(true);
        break;
    }
  }

  private save() {
    this.package.status = true;
    this.packageService.savePackage(this.package).subscribe(newPackage => {
      console.log(newPackage);
    });
  }
}
<div class="row px-3">
  <div class="col-6 col-sm-6 my-3">
    <label class="ps-3 mb-1">بارکد</label>
    <div>
      <input type="text" class="form-control" [(ngModel)]="package.barcode">
    </div>
  </div>
  <div class="col-6 col-sm-6 my-3">
    <label class="ps-3 mb-1">رنگ</label>
    <div>
      <input type="color" class="form-control" [(ngModel)]="package.color">
    </div>
  </div>
  <div class="col-12 col-sm-12 my-3">
    <app-operation-bottom
      (buttonEmitter)="buttonClicked($event)"
    ></app-operation-bottom>
  </div>
  <!--  <div class="col-6 col-sm-6"></div>-->
</div>
