import {Component, EventEmitter, Input, OnInit, Output} from '@angular/core';

@Component({
  selector: 'app-header-tools',
  templateUrl: './header-tools.component.html',
  styleUrls: ['./header-tools.component.css']
})
export class HeaderToolsComponent implements OnInit {

  @Input() editMode: boolean = false;
  @Input() showPlus: boolean = !this.editMode;
  @Input() showSearch: boolean = true;
  @Output() editModeEmitter: EventEmitter<boolean> = new EventEmitter<boolean>();
  @Output() reloadEmitter: EventEmitter<boolean> = new EventEmitter<boolean>();
  @Output() searchModeEmitter: EventEmitter<boolean> = new EventEmitter<boolean>();

  constructor() {
  }

  ngOnInit(): void {
  }

  switchToEditMode() {
    if (!this.editMode) {
      this.editModeEmitter.emit(true);
      this.editMode = true;
    }
  }

  switchToListMode() {
    if (this.editMode) {
      this.editModeEmitter.emit(false);
      this.editMode = false;
    }
  }

  reload() {
    this.reloadEmitter.emit(true);
    if (this.editMode) {
    } else {
    }
  }

  switchToSearchMode() {
    this.searchModeEmitter.emit(true);
  }
}
<h4><i class="fa fa-list m-1 fa-rotate-180" (click)="switchToListMode()" *ngIf="editMode"></i></h4>
<h4><i class="fa fa-plus m-1" (click)="switchToEditMode()" *ngIf="!editMode && showPlus"></i></h4>
<h4><i class="fa fa-search m-1" *ngIf="!editMode && showSearch" (click)="switchToSearchMode()"></i></h4>
<h4><i class="fa fa-refresh m-1" (click)="reload()"></i></h4>
