import {Component, EventEmitter, OnInit, Output} from '@angular/core';
import {Forwarding} from "./forwarding";
import {ForwardingService} from "./forwarding.service";

@Component({
  selector: 'app-forwarding-edit',
  templateUrl: './forwarding-edit.component.html',
  styleUrls: ['./forwarding-edit.component.css']
})
export class ForwardingEditComponent implements OnInit {

  forwarding: Forwarding = new Forwarding();
  @Output() switchToGrid: EventEmitter<boolean> = new EventEmitter();
  emptyString: string = '';

  constructor(
    private forwardingService: ForwardingService,
  ) {
  }

  ngOnInit(): void {
  }

  buttonClicked($event: string) {
    switch ($event) {
      case 'save':
        this.save();
        break;
      case 'reload':
        this.clearForm();
        break;
      case 'return':
        this.switchToGrid.emit(false);
        break;
    }
  }

  save() {
    this.forwardingService.saveForwarding(this.forwarding).subscribe(forwarding => {
      this.switchToGrid.emit(false);
    });
  }

  clearForm() {
    this.forwarding = new Forwarding();
    this.emptyString = '';
  }
}
<div class="row px-3">
  <div class="col-4 col-sm-4 my-3">
    <label class="ps-3 mb-1">حمل کننده</label>
    <ceid-select
      [selectClass]="'form-control'"
      [rest]="'rest/person/getAll'"
      [isBaseInformation]="false"
      [titleName]="'fullName'"
      (sendValue)="forwarding.transporterId = $event"
      [selectValue]="emptyString"
    ></ceid-select>
  </div>
  <div class="col-4 col-sm-4 my-3">
    <label class="ps-3 mb-1">انبار مقصد</label>
    <ceid-select
      [selectClass]="'form-control'"
      [rest]="'rest/warehouse/getAll'"
      [isBaseInformation]="false"
      [titleName]="'name'"
      (sendValue)="forwarding.warehouseId = $event"
      [selectValue]="emptyString"
    ></ceid-select>
  </div>
  <div class="col-4 col-sm-4 my-3">
    <label class="ps-3 mb-1">تحویل گیرنده</label>
    <ceid-select
      [selectClass]="'form-control'"
      [rest]="'rest/person/getAll'"
      [isBaseInformation]="false"
      [titleName]="'fullName'"
      (sendValue)="forwarding.receiverId = $event"
      [selectValue]="emptyString"
    ></ceid-select>
  </div>
  <div class="col-4 col-sm-4 my-3">
    <label class="ps-3 mb-1">بسته بندی</label>
    <ceid-select
      [selectClass]="'form-control'"
      [rest]="'rest/package/getAll'"
      [titleName]="'color'"
      (sendValue)="forwarding.packagesId = $event"
      [selectValue]="emptyString"
    ></ceid-select>
  </div>
  <div class="col-4 col-sm-4 my-3">
    <label class="ps-3 mb-1">وسیله حمل و نقل</label>
    <ceid-select
      [selectClass]="'form-control'"
      [rest]="'rest/baseInformation/getAll'"
      [parent]="'57f2560e-2839-4f48-a4a4-0254754e0ef5'"
      [isBaseInformation]="true"
      [titleName]="'label'"
      (sendValue)="forwarding.carrierId = $event"
      [selectValue]="emptyString"
    ></ceid-select>
  </div>
  <div class="col-4 col-sm-4 my-3">
    <label class="ps-3 mb-1">تاریخ ارسال</label>
    <input class="form-control" [(ngModel)]="forwarding.transportDate">
  </div>
  <div class="col-4 col-sm-4 my-3">
    <label class="ps-3 mb-1">تاریخ تحویل</label>
    <input class="form-control" [(ngModel)]="forwarding.receiveDate">
  </div>
  <div class="col-4 col-sm-4 my-3">
    <label class="ps-3 mb-1">شماره وسیله</label>
    <input class="form-control" [(ngModel)]="forwarding.carrierNumber">
  </div>
  <div class="col-4 col-sm-4 my-3">
    <label class="ps-3 mb-1">توضیحات</label>
    <textarea
      class="form-control" rows="3"
      [(ngModel)]="forwarding.description"
    ></textarea>
  </div>
  <div class="col-12 col-sm-12 my-3">
    <app-operation-bottom
      (buttonEmitter)="buttonClicked($event)"
    ></app-operation-bottom>
  </div>
  <!--  <div class="col-6 col-sm-6"></div>-->
</div>
