import {Component, EventEmitter, Input, OnInit, Output, ViewChild} from '@angular/core';
import {Warehouse} from './warehouse';
import {WarehouseService} from './warehouse.service';

@Component({
  selector: 'app-warehouse-edit',
  templateUrl: './warehouse-edit.component.html',
  styleUrls: ['./warehouse-edit.component.css']
})
export class WarehouseEditComponent implements OnInit {

  public warehouse: Warehouse;
  public editLoadId?: string;
  @Input() public warehouseId: string = '';
  @Output() public editModeEmitter: EventEmitter<boolean> = new EventEmitter<boolean>();
  showHideLoaderFlag = false;

  // @ViewChild('jqxLoader', {static: false}) jqxLoader: jqxLoaderComponent;

  constructor(
    private warehouseService: WarehouseService
  ) {
    // @ts-ignore
    // this.jqxLoader = new jqxLoaderComponent();
    // tslint:disable-next-line:new-parens
    this.warehouse = new Warehouse;
  }

  ngOnInit(): void {
    // tslint:disable-next-line:triple-equals
    if (this.warehouseId != '') {
      this.onLoad();
    }
  }

  // tslint:disable-next-line:typedef
  onLoad() {
    this.warehouse = new Warehouse();
    this.warehouseService.loadWarehouse(this.warehouseId).subscribe(warehouse => {
      this.warehouse = warehouse;
    });
  }

  // tslint:disable-next-line:typedef
  save() {
    this.showHideLoader();
    this.warehouseService.saveWarehouse(this.warehouse).subscribe(data => {
      // tslint:disable-next-line:label-position
      success: {
        this.switchToGrid();
      }
      // tslint:disable-next-line:label-position
      final: {
        // setTimeout(() => {
        //   this.showHideLoader();
        // },5000);
      }
    });
  }

  // tslint:disable-next-line:typedef
  switchToGrid() {
    this.warehouse = new Warehouse();
    this.editModeEmitter.emit(false);
  }

  // tslint:disable-next-line:typedef
  showHideLoader() {
    console.log('show');
    // this.jqxLoader.open();
    // this.showHideLoaderFlag ? this.jqxLoader?.open() : this.jqxLoader?.close();
  }
}
<div class="row m-0 p-0 col-12 col-sm-12">
  <div class="row m-0 mt-3 p-0 col-12 col-sm-12">
    <div class="col-6 col-sm-6">
      <label class="col-12 col-sm-12 control-label text-danger">نام انبار</label>
      <div class="col-12 col-sm-12">
        <input type="text" class="form-control" [(ngModel)]="warehouse.name">
      </div>
    </div>
<!--    <div class="col-6 col-sm-6">-->
<!--      <label class="col-12 col-sm-12 control-label text-danger">ظرفیت</label>-->
<!--      <div class="col-12 col-sm-12">-->
<!--        <input type="text" class="form-control" [(ngModel)]="warehouse.capacity">-->
<!--      </div>-->
<!--    </div>-->
    <div class="col-6 col-sm-6 px-4">
      <label class="col-12 col-sm-12 control-label">وضعیت فعالیت</label>
      <input type="checkbox" [(ngModel)]="warehouse.status">
    </div>
    <div class="col-6 col-sm-6">
    </div>
  </div>
  <div class="row m-0 mt-3 p-0 col-12 col-sm-12">
    <span style="width: 8rem;">
    <button class="btn btn-primary w-100" (click)="save()">
      ثبت
      <i class="fa fa-save"></i>
    </button>
    </span>
    <span style="width: 8rem;">
    <button class="btn btn-danger w-100">
      بازنشانی
      <i class="fa fa-refresh"></i>
    </button>
    </span>
    <span style="width: 8rem;">
    <button class="btn btn-warning w-100" (click)="switchToGrid()">
      بازگشت
      <i class="fa fa-backspace"></i>
    </button>
    </span>
  </div>
</div>
