import {Component, EventEmitter, OnInit, Output} from '@angular/core';

@Component({
  selector: 'app-operation-bottom',
  templateUrl: './operation-bottom.component.html',
  styleUrls: ['./operation-bottom.component.css']
})
export class OperationBottomComponent implements OnInit {

  @Output() public buttonEmitter: EventEmitter<string> = new EventEmitter();

  constructor() {
  }

  ngOnInit(): void {
  }

  buttonClicked(buttonClicked: string) {
    this.buttonEmitter.emit(buttonClicked);
  }

}
<div class="row m-0 p-0 bg-white col-sm-12 col-12">
  <div class="row m-0 mt-5 mb-3 p-0 col-12-col-sm-12">
    <span class="col-sm-3 col-3">
      <button type="button"
              (click)="buttonClicked('save')"
              class="btn btn-primary w-100">
        ذخیره
      <i class="fa fa-save"></i>
      </button>
    </span>
    <span class="col-sm-3 col-3">
      <button type="button"
              (click)="buttonClicked('reload')"
              class="btn btn-warning w-100">
        بازنشانی
      <i class="fa fa-refresh"></i>
      </button>
    </span>
    <span class="col-sm-3 col-3">
      <button type="button"
              (click)="buttonClicked('return')"
              class="btn btn-danger w-100">
        بازگشت
      <i class="fa fa-backspace"></i>
      </button>
    </span>
  </div>
</div>
