import {Component, EventEmitter, Input, OnInit, Output} from '@angular/core';
import {Ware} from "../ware/ware";
import {WarehouseInventory} from "./warehouse-inventory";
import {WarehouseInventoryService} from "./warehouse-inventory.service";
import {Person} from "../person/person";
import {PersonService} from "../person/person.service";
import {WareService} from "../ware/ware.service";
import {JalaliPipe} from "../general/utility/pipeTools/dateTimeTools/jalali-pipe";
import { FormControl } from '@angular/forms';

@Component({
  selector: 'app-warehouse-inventory-edit',
  templateUrl: './warehouse-inventory-edit.component.html',
  styleUrls: ['./warehouse-inventory-edit.component.css']
})
export class WarehouseInventoryEditComponent implements OnInit {

  public warehouseInventory: WarehouseInventory = new WarehouseInventory();
  @Input() public wares?: Ware[];
  public producers?: Person[];
  @Input() warehouseId?: string;
  @Output() public switchToGrid: EventEmitter<boolean> = new EventEmitter<boolean>();

  expirationDate = new FormControl();
  entryDate = new FormControl();
  dataValue = new FormControl();

  constructor(
    private warehouseInventoryService: WarehouseInventoryService,
    private personService: PersonService,
    private wareService: WareService,
    private dateTransformer: JalaliPipe,
  ) {
  }

  ngOnInit(): void {
    this.warehouseInventory.warehouseId = this.warehouseId;
    this.warehouseInventory.color = '#e66465';
    this.loadProducers();
    this.loadWares();
  }

  buttonClicked($event: string) {
    switch ($event) {
      case 'save':
        this.warehouseInventory.entryDate = this.entryDate.value;
        this.warehouseInventory.expirationDate = this.expirationDate.value;
        // this.save();
        console.log(this.warehouseInventory);
        break;
      case 'reload':
        break;
      case 'return':
        this.switchToGrid.emit(true);
        break;
    }
  }

  private loadProducers() {
    this.personService.getAllPerson().subscribe(persons => {
      this.producers = persons;
    });
  }

  private save() {
    this.warehouseInventoryService.saveWarehouseInventory(this.warehouseInventory).subscribe(warehouseInventory => {
      this.switchToGrid.emit(true);
    });
  }

  private loadWares() {
    this.wareService.getAllWare().subscribe(wares => {
      this.wares = wares;
    });
  }
}
<div class="col-12 col-sm-12">
  <div class="bg-white row">
    <!--  <div class="row m-0 p-0 col-12 col-sm-12 mb-3 bg-white">-->
    <div class="col-6 col-sm-6">
      <label class="control-label">کالا</label>
      <select class="form-control" [(ngModel)]="warehouseInventory.wareId">
        <option value="">كالای مد نظر را انتخاب نمایید</option>
        <option *ngFor="let ware of wares" value="{{ ware.id }}">{{ ware.name }}</option>
      </select>
    </div>
    <div class="col-6 col-sm-6">
      <label class="control-label">تهیه کننده</label>
      <select class="form-control" [(ngModel)]="warehouseInventory.producerId">
        <option value="">تهیه کننده را انتخاب نمایید</option>
        <option *ngFor="let producer of producers"
                value="{{ producer.id }}">{{ producer.firstName }} {{ producer.lastName }}</option>
      </select>
    </div>
    <div class="col-6 col-sm-6">
      <label class="control-label">سریال</label>
      <input type="text" class="form-control" [(ngModel)]="warehouseInventory.serial">
    </div>
    <div class="col-3 col-sm-3">
      <label class="control-label">شماره قرارداد</label>
      <input type="text" class="form-control" [(ngModel)]="warehouseInventory.contractNumber">
    </div>
    <div class="col-3 col-sm-3">
      <label class="control-label">رنگ</label>
      <select class="form-control" [(ngModel)]="warehouseInventory.color">
        <option value="#956126" style="background-color: #956126"></option>
        <option value="#785268" style="background-color: #d85268"></option>
        <option value="#a2c3f4" style="background-color: #a2c3f4"></option>
        <option value="#" style="background-color: #956126"></option>
        <option value="#" style="background-color: #956126"></option>
        <option value="#" style="background-color: #956126"></option>
        <option value="#" style="background-color: #956126"></option>
        <option value="#" style="background-color: #956126"></option>
        <option value="#" style="background-color: #956126"></option>
      </select>
    </div>
    <div class="col-6 col-sm-6">
      <label class="control-label">مبدأ خرید</label>
      <ceid-select
        [selectClass]="'form-control'"
        [rest]="'rest/baseInformation/getAll'"
        [parent]="'edfca745-5eee-4169-939d-5e828f291e3d'"
        [isBaseInformation]="true"
        [titleName]="'label'"
        (sendValue)="warehouseInventory.buyOriginId = $event"
        [selectValue]="''"
      ></ceid-select>
    </div>
    <div class="col-3 col-sm-3">
      <label class="control-label">تاریخ ورود</label>
      <!--<dp-date-picker
        class=""
        dir="rtl"
        [(ngModel)]="entryDate"
        mode="day"
        placeholder=""
        [config]="{
        format: 'YYYY/MM/DD'
        }"
        theme="dp-material">
      </dp-date-picker>-->
      <ng-persian-datepicker>
        <input type="text" class="form-control" [formControl]="dataValue">
      </ng-persian-datepicker>
    </div>
    <!--    <div class="col-3 col-sm-3">-->
    <!--      <label class="control-label">تاریخ خروج</label>-->
    <!--      <dp-date-picker-->
    <!--        class="w-100"-->
    <!--        dir="rtl"-->
    <!--        [(ngModel)]="exitDate"-->
    <!--        mode="day"-->
    <!--        placeholder=""-->
    <!--        [config]="{-->
    <!--        format: 'YYYY/MM/DD'-->
    <!--        }"-->
    <!--        theme="dp-material">-->
    <!--      </dp-date-picker>-->
    <!--    </div>-->
    <div class="col-3 col-sm-3">
      <label class="control-label">تاریخ انقضاء</label>
      <!--<dp-date-picker
        [dir]="'rtl'"
        [(ngModel)]="expirationDate"
        mode="day"
        placeholder=""
        [config]="{
        format: 'YYYY/MM/DD'
        }"
        theme="dp-material">
      </dp-date-picker>-->
    </div>
    <!--    <div class="col-6 col-sm-6">-->
    <!--    </div>-->
    <div class="col-6 col-sm-6">
      <label class="">متعلقات</label>
      <textarea class="form-control" [(ngModel)]="warehouseInventory.appurtenance"></textarea>
    </div>
    <div class="col-6 col-sm-6">
      <label class="">توضیحات</label>
      <textarea class="form-control" [(ngModel)]="warehouseInventory.description"></textarea>
    </div>
    <div class="col-6 col-sm-6"></div>
    <div class="col-6 col-sm-6"></div>
    <app-operation-bottom
      (buttonEmitter)="buttonClicked($event)"
    ></app-operation-bottom>
  </div>
</div>
